import React, { Component } from "react";
import {
  FiUsers,
  FiMonitor,
  FiSmartphone,
  FiZoomIn,
  FiX,
} from "react-icons/fi";

const ServiceList = [
  {
    icon: <FiMonitor />,
    title: "Developpement Site Internet",
    description: `Je conçois un site internet responsive*.`,
  },
  {
    icon: <FiSmartphone />,
    title: "Developpement Application Mobile",
    description:
      "Je développe une application mobile intégrant les fonctionnalités dont vous avez besoin, compatible avec iOS et Android.",
  },
  {
    icon: <FiZoomIn />,
    title: "Garantie & Maintenance",
    description: `Je m'engage à vous fournir un code fonctionnel et prend en considération les éventuels changements désirés.`,
  },
];
const explication1 = `*responsive: design adaptatif selon les dimensions des supports : TV,ordinateurs,tablettes,téléphones...`;
class ServiceThree extends Component {
  render() {
    const { column } = this.props;
    const ServiceContent = ServiceList.slice(0, this.props.item);

    return (
      <React.Fragment>
        <div className="row">
          {ServiceContent.map((val, i) => (
            <div className={`${column}`} key={i}>
              <a href="/service-details">
                <div className="service service__style--2">
                  <div className="icon">{val.icon}</div>
                  <div className="content">
                    <h3 className="title">{val.title}</h3>
                    <p>{val.description}</p>
                  </div>
                </div>
              </a>
            </div>
          ))}
          <p style={{ color: "#ffffff" }}>{explication1}</p>
        </div>
      </React.Fragment>
    );
  }
}
export default ServiceThree;
