import React, { Component } from "react";
import {
  FiUsers,
  FiMonitor,
  FiSmartphone,
  FiZoomIn,
  FiSmile,
  FiUserCheck,
} from "react-icons/fi";

const ServiceList = [
  {
    icon: <FiMonitor />,
    title: "Developpement Site Internet",
    description: `Je conçois un site internet responsive* complet.`,
  },
  {
    icon: <FiSmartphone />,
    title: "Developpement Application Mobile",
    description:
      "Je développe une application mobile intégrant les fonctionnalités nécessaires à votre activité, compatible avec iOS et Android.",
  },
  {
    icon: <FiUserCheck />,
    title: "Sur mesure",
    description:
      "Selon la complexité du projet et vos besoins, j'intègre une base de données, crée les API** nécessaires.",
  },
  {
    icon: <FiZoomIn />,
    title: "Garantie & Maintenance",
    description: `Je m'engage à vous fournir un code fonctionnel testé et prend en considération les éventuels changements désirés.`,
  },
];
const explication1 = `*Design adaptatif selon les dimensions des supports : TV, ordinateurs, tablettes,téléphones...`;
const explication2 = `**Application Programming Interface est un intermédiaire qui permet à deux programmes ou applications de communiquer et d'échanger des informations entre eux. Exemple:Traitement/récupération de données `;
class ServiceThree extends Component {
  render() {
    const { column } = this.props;
    const ServiceContent = ServiceList.slice(0, this.props.item);

    return (
      <React.Fragment>
        <div className="row">
          {ServiceContent.map((val, i) => (
            <div className={`${column}`} key={i}>
              <a href="/service-details">
                <div className="service service__style--2">
                  <div className="icon">{val.icon}</div>
                  <div className="content">
                    <h3 className="title">{val.title}</h3>
                    <p>{val.description}</p>
                  </div>
                </div>
              </a>
            </div>
          ))}
        </div>
        <p style={{ color: "#999AA6", marginTop: 10, marginLeft: 20 }}>
          {explication1}
        </p>
        <p style={{ color: "#999AA6", marginTop: 10, marginLeft: 20 }}>
          {explication2}
        </p>
      </React.Fragment>
    );
  }
}
export default ServiceThree;
